const image = document.querySelectorAll(".small-img");
const etst = document.createElement('button');
etst.style.width = "100%";
etst.style.borderRadius = "30px";
etst.style.marginTop = "-30px";
etst.style.border = "none";
etst.style.fontFamily =  "yekan";
etst.style.cursor =  "pointer";
etst.style.fontSize =  "30px";
etst.style.height =  "60px";
etst.style.backgroundColor =  "#c9c9c9";
const elementpic = document.createElement('div');
etst.innerText = "حذف تصویر";
image.forEach(image1=>{
    image1.addEventListener('click',()=>{
        // elementpic.classList.add("image_click");
        elementpic.style.backgroundImage = `url(${image1.src})`;
        elementpic.style.position = "fixed";
        elementpic.style.top = "0";
        elementpic.style.display = "flex";
        elementpic.style.marginLeft = "80px";
        elementpic.style.justifyContent = "center";
        elementpic.style.backdropFilter = "blur(20px)";
        elementpic.style.marginLeft = "80px";
        elementpic.style.width = "60%";
        elementpic.style.left = "0";
        elementpic.style.marginTop = "150px";
        elementpic.style.borderRadius = "30px";
        elementpic.style.height = "32%";
        elementpic.style.backgroundSize = "contain";
        elementpic.style.backgroundPosition = "center";
        elementpic.style.backgroundRepeat = "no-repeat";
        elementpic.style.zIndex = "1000";
        elementpic.appendChild(etst);
        document.body.appendChild(elementpic);
        elementpic.addEventListener('click', () => {
        document.body.removeChild(elementpic);
        });
    });
})




